from PIL import Image

symbols = 'Mx*=+~· '

def get_symbol(color):
    #я знаю, что это не лучшее решение
    #i know, it bad solution
    if color < 31:
        return ' '
    if color >= 31 and color < 62:
        return '·'
    if color >= 62 and color < 93:
        return '~'
    if color >= 93 and color < 124:
        return '+'
    if color >= 124 and color < 155:
        return '≈'
    if color >= 155 and color < 186:
        return '*'
    if color >= 186 and color < 217:
        return 'x'
    if color >= 217:
        return 'M'

